'use strict';
import pluralize from 'pluralize';
import shortid from 'shortid';

/**
 * given a type and context resolve the collection name
 * @param {string} type - this instance type
 * @param {object} context - the current context
 * @returns {string} the collection name to use
 */
export function getCollectionName(type, context) {
  var bucketId = 'global';
  if (context.bucket) {
    bucketId = context.bucket._id;
  }
  return context.environment + ":" + bucketId + ":" + pluralize(type).toLowerCase();
}


function ensureId(json) {
  if (!json._id) {
    json._id = shortid();
  }
  return json;
}

function ensureType(type, json) {
  json._type = type.toLowerCase();
  return json;
}

/**
 * adds any required fields to the instance
 * @param {string} type - the type of the instance
 * @param {object} instance - the instance to transform
 * @returns {object} the transformed instance
 */
export function requiredFieldsTransformer(type, instance) {
  return ensureId(ensureType(type, instance));
}

/**
 * adds timestamps to an instance
 * @param {object} instance - the instance to transform
 * @returns {object} the transformed instance
 */

export function timestampsTransformer(instance) {
  instance._createdDate = new Date();
  instance._updatedDate = new Date();
  return instance;
}

'use strict';
import Bluebird from 'bluebird';
import mongodb from 'mongodb';
import config from 'config';
import logger from '@hoist/logger';
Bluebird.promisifyAll(mongodb.MongoClient);
/**
 * marshalling class for connection to mongodb
 */
class MongoConnection {
  /**
   * create a new instance
   */
  constructor() {
    this._mongoClient = mongodb.MongoClient;
    this._logger = logger.child({
      cls: this.constructor.name
    });
  }

  _openConnection() {
    return this._mongoClient
      .connectAsync(config.get('Hoist.mongo.applications'))
      .then((connection) => {
        //close connection after 2 seconds of inactivity
        this._logger.info('connection opened');
        this._connection = connection;
        this._connection.on('close', () => {
          delete this._connection;
        });
        Bluebird.promisifyAll(this._connection);
        return connection;
      });
  }

  /**
   * close any open connection to mongo
   * @returns {Promise}
   */
  close() {
    if (!this._connection) {
      return Promise.resolve();
    }
    let connection = this._connection;
    delete this._connection;
    return connection.closeAsync();
  }

  /**
   * Attempt to reuse mongo connection or open one if it doesn't exist
   * @returns {Promise}
   */
  open() {
    if (this._connection) {
      return Promise.resolve(this._connection);
    } else {
      return this._openConnection();
    }

  }
}

export default new MongoConnection();

'use strict';
import util from 'util';
import mongoConnection from './mongo_connection';
import Context from '@hoist/context';
import {
  requiredFieldsTransformer,
  timestampsTransformer,
  getCollectionName
}
from './helpers';
import Bluebird from 'bluebird';
import logger from '@hoist/logger';
/**
 * application data api pipeline
 */
class DataPipeline {
  /**
   * create a new pipeline
   */
  constructor() {
    this._connection = mongoConnection;
    let applicationId;
    let context = Context.current();
    if (context && context.application) {
      applicationId = context.application._id;
    }
    this._logger = logger.child({
      cls: this.constructor.name,
      applicationId: applicationId
    });
  }

  /**
   * save an object or set of objects as a type
   * @param {string} type - the object types
   * @param {object|Array<object>} objOrArray - the object(s) to save
   * @returns {Promise}
   */
  save(type, objOrArray) {
    var arrayObj = [].concat(objOrArray);
    this._logger.info('saving data');
    return Context.get().then((context) => {
      this._logger.info('opening connection');
      return this._connection.open().then((connection) => {
        return Promise.all(arrayObj.map((obj) => {
          return Promise.resolve(requiredFieldsTransformer(type, obj))
            .then((data) => {
              return timestampsTransformer(data);
            }).then((data) => {
              this._logger.info('sending to mongo');
              var db = connection.db(context.application.dataKey);
              var collection = Bluebird.promisifyAll(db.collection(getCollectionName(data._type, context)));
              var createdDate = new Date();
              if (data._createdDate) {
                createdDate = data._createdDate;
                delete data._createdDate;
              }
              var id = data._id;
              delete data._id;
              return collection.updateOneAsync({
                _id: id
              }, {
                $set: data,
                $setOnInsert: {
                  _createdDate: createdDate
                }
              }, {
                upsert: true
              }).then(() => {
                this._logger.info('finding post save');
                return collection.findOneAsync({
                  _id: id
                });
              });
            });

        }));
      });
    }).then((savedData) => {
      if (!util.isArray(objOrArray)) {
        if (savedData.length < 2) {
          if (savedData.length < 1) {
            return null;
          } else {
            return savedData[0];
          }
        }
      }
      return savedData;
    });
  }

  /**
   * finds all objects of type matching the given query
   * @param {string} type - the object types
   * @param {object} query - a mongo style query
   * @returns {Promise}
   */
  find(type, query) {
    this._logger.info('finding data');
    return Context.get().then((context) => {
      this._logger.info('opening connection data');
      return this._connection.open().then((connection) => {
        //throw new Error();
        var db = connection.db(context.application.dataKey);
        var collection = db.collection(getCollectionName(type, context));
        return Bluebird.promisifyAll(collection.find(query)).toArrayAsync()
          .then((results) => {
            this._logger.info('retrieved data');
            return Promise.resolve(results);
          });
      });
    });
  }

  /**
   * finds a single object of type matching the given query
   * @param {string} type - the object types
   * @param {object} query - a mongo style query
   * @returns {Promise}
   */
  findOne(type, query) {
    this._logger.info('finding single');
    return Context.get().then((context) => {
      this._logger.info('opening connection');
      return this._connection.open().then((connection) => {
        var db = connection.db(context.application.dataKey);
        var collection = Bluebird.promisifyAll(db.collection(getCollectionName(type, context)));
        return collection.findOneAsync(query)
          .then((results) => {
            this._logger.info('retrieved data');
            return results;
          });
      });
    });
  }

  /**
   * removes all objects of type matching the given query
   * @param {string} type - the object types
   * @param {object} query - a mongo style query
   * @returns {Promise}
   */
  remove(type, query) {
    this._logger.info('removing data');
    return Context.get().then((context) => {
      this._logger.info('opening connection');
      return this._connection.open().then((connection) => {
        var db = connection.db(context.application.dataKey);
        var collection = Bluebird.promisifyAll(db.collection(getCollectionName(type, context)));
        return collection.removeAsync(query)
          .then((results) => {
            this._logger.info('removed data');
            return results;
          });
      });
    });
  }
}

